import React, { useState, useRef, useEffect} from 'react';
import TextareaAutosize from 'react-autosize-textarea';

import './recommendquestion.css'


export const Recommendquestion = (props) => {

    const [isEdit, setIsEdit] = useState(false);
    const [prevQuestion, setPrevQuestion] = useState("");
    const [currentQuestion, setCurrentQuestion] = useState("");
    const textAreaRef = useRef(null);

    useEffect(() => {
        if(textAreaRef.current) {
            textAreaRef.current.dispatchEvent(new Event('input', { bubbles: true }));
        }
    }, [currentQuestion]);

    const currentQuestionHandler = (e) => {
        e.preventDefault();
        setCurrentQuestion(e.target.value);
    }

    return(
        <>
            <div className='recommendquestionbox'>
                <div className='recommendquestion'>
                    {isEdit ? <TextareaAutosize className='editAnswer' ref={textAreaRef} value={currentQuestion} onChange={currentQuestionHandler} onResize={(e) => {}}/> : <div>{props.answer}</div>}
                    {props.question}
                    <img className='edit' src="images/edit.svg" alt="handle"/>
                </div>
                <button className='addBtn' onClick={props.addRecommendQuestion}>
                    Add +
                </button>
            </div>
        </>
    )
}
